import React, { useState } from 'react';
import './css/SearchBar.css';

const livros = [
    { nome: "Mateus", capitulos: 28 },
    { nome: "Marcos", capitulos: 16 },
    { nome: "Lucas", capitulos: 24 },
    { nome: "João", capitulos: 21 },
    { nome: "Atos", capitulos: 28 },
    { nome: "Romanos", capitulos: 16 },
    { nome: "1 Coríntios", capitulos: 16 },
    { nome: "2 Coríntios", capitulos: 13 },
    { nome: "Gálatas", capitulos: 6 },
    { nome: "Efésios", capitulos: 6 },
    { nome: "Filipenses", capitulos: 4 },
    { nome: "Colossenses", capitulos: 4 },
    { nome: "1 Tessalonicenses", capitulos: 5 },
    { nome: "2 Tessalonicenses", capitulos: 3 },
    { nome: "1 Timóteo", capitulos: 6 },
    { nome: "2 Timóteo", capitulos: 4 },
    { nome: "Tito", capitulos: 3 },
    { nome: "Filemom", capitulos: 1 },
    { nome: "Hebreus", capitulos: 13 },
    { nome: "Tiago", capitulos: 5 },
    { nome: "1 Pedro", capitulos: 5 },
    { nome: "2 Pedro", capitulos: 3 },
    { nome: "1 João", capitulos: 5 },
    { nome: "2 João", capitulos: 1 },
    { nome: "3 João", capitulos: 1 },
    { nome: "Judas", capitulos: 1 },
    { nome: "Apocalipse", capitulos: 22 },
];

const SearchBar = ({ onSearch }) => {
    const [searchTermVersiculo, setSearchTermVersiculo] = useState('');
    const [searchTermlivro, setSearchTermLivro] = useState('');

    const handleInputChange = (e) => {
        setSearchTermVersiculo(e.target.value);
    };

    const handleSelectChange = (e) => {
        setSearchTermLivro(e.target.value);
    };

    const handleSearchClick = () => {
        const searchQuery = `${searchTermlivro} ${searchTermVersiculo}`;
        onSearch(searchQuery);
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleSearchClick();
        }
    };

    const handleGenerateRandom = () => {
        const randomBook = livros[Math.floor(Math.random() * livros.length)];
        const randomChapter = Math.floor(Math.random() * randomBook.capitulos) + 1;
        const randomVerse = Math.floor(Math.random() * 50) + 1; // Supondo um limite de 50 versículos por capítulo

        const searchQuery = `${randomBook.nome} ${randomChapter}:${randomVerse}`;
        onSearch(searchQuery);
    };

    return (
        <div className="search-bar">
            <label htmlFor="livros-nt">Selecione um livro do Novo Testamento:</label>
            <select 
                id="livros-nt" 
                name="livros-nt" 
                onChange={handleSelectChange}
                value={searchTermlivro}
            >
                <option value="" disabled>Escolha um livro</option>
                {livros.map((livro, index) => (
                    <option key={index} value={livro.nome.toLowerCase()}>
                        {livro.nome}
                    </option>
                ))}
            </select>

            <input
                type="text"
                value={searchTermVersiculo}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
                placeholder="Pesquise um versículo (Ex: 3:16)."
                className="search-bar__input"
            />

            <button
                className="search-bar__button"
                onClick={handleSearchClick}
            >
                Buscar
            </button>

            <button
                className="search-bar__button"
                onClick={handleGenerateRandom}
            >
                Gerar Aleatório
            </button>
        </div>
    );
};

export default SearchBar;
