import React, { useState } from 'react';
import bibleData from '../../../data/bible.json';

const Verses = () => {
    const [selectedBook, setSelectedBook] = useState(null);
    const [selectedChapter, setSelectedChapter] = useState(null);

    const handleBookSelect = (book) => {
        setSelectedBook(book);
        setSelectedChapter(null); // Reset chapter selection when a new book is selected
    };

    const handleChapterSelect = (chapter) => {
        setSelectedChapter(chapter);
    };

    return (
        <div>
            <h1>Versículos</h1>
            <div className="testaments">
                <h2>Antigo Testamento</h2>
                <ul>
                    {Object.keys(bibleData["Old Testament"]).map((book) => (
                        <li key={book} onClick={() => handleBookSelect(book)}>
                            {book}
                        </li>
                    ))}
                </ul>
                <h2>Novo Testamento</h2>
                <ul>
                    {Object.keys(bibleData["New Testament"]).map((book) => (
                        <li key={book} onClick={() => handleBookSelect(book)}>
                            {book}
                        </li>
                    ))}
                </ul>
            </div>
            
            {selectedBook && (
                <div className="chapters">
                    <h3>{selectedBook}</h3>
                    <ul>
                        {bibleData["Old Testament"][selectedBook].map((chapter, index) => (
                            <li key={index} onClick={() => handleChapterSelect(chapter)}>
                                Capítulo {chapter.chapter}
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {selectedChapter && (
                <div className="verses">
                    <h4>Versículos do Capítulo {selectedChapter.chapter}</h4>
                    <ul>
                        {[...Array(selectedChapter.verses)].map((_, verse) => (
                            <li key={verse}>Versículo {verse + 1}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Verses;
