import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { navigateVerse } from '../slices/bibleSlice';
import bible from '../data/bible.json';

const VerseView = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const selectedBook = useSelector((state) => state.bible.selectedBook);
    const selectedChapter = useSelector((state) => state.bible.selectedChapter); 
    const selectedVerse = useSelector((state) => state.bible.selectedVerse);

    if (!selectedBook || !selectedChapter || !selectedVerse) 
        return <p>Selecione um versículo na lista.</p>;

    const chapterData = bible[selectedBook]?.find(
        (chapter) => chapter.chapter === selectedChapter.chapter
    );

    if (!chapterData) {
        return <p>{bible[selectedBook]}: Capítulo não encontrado. Verifique a seleção e tente novamente.</p>;
    }

    const isFirstVerse = selectedVerse === 1;
    const isLastVerse = selectedVerse === chapterData.verses;

    const handleNextVerse = () => {
        if(!isLastVerse) {
            dispatch(navigateVerse({ direction: 'next', bibleData: bible}));
        }
    };

    const handlePrevVerse = () => {
        if (!isFirstVerse) {
            dispatch(navigateVerse({ direction: 'prev', bibleData: bible}));
        }
    };

    return (
        <div>
            <h1>{selectedBook}</h1>
            <h2>Capítulo {selectedChapter.chapter}, Versículo {selectedVerse}</h2>

            {/* Renderiza o conteúdo do versículo (ainda não implementado) */}
            <p>Versículo: {selectedVerse}</p>
            <div className='navigation'>
                {!isFirstVerse && (
                    <button onClick={handlePrevVerse}>Versículo Anterior</button>
                )}
                {!isLastVerse && (
                    <button onClick={handleNextVerse}>Próximo Versículo</button>
                )}
            </div>

            <button onClick={() => navigate('/')}>Voltar</button>
        </div>
    );
};

export default VerseView;
