export const fetchVerses = async (searchTerm = '') => {

    try {
        const regex = /^(\w+)\s*(\d{2})[\s:.]?\s*(\d{2})$/;
    
        const result = searchTerm.match(regex);
        
        if (result) {
            const book = result[1];
            const chap = result[2];
            const vers = result[3];
            
            return { book, chap, vers };
        } else {
            throw new Error('Formato inválido');
        }
        const response = await fetch(`https://bible-api.com/${book}%20${chap}:${vers}?translation=almeida`); 
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('Erro ao buscar versículos:', error);
        return [];
    }
};
